const $injector = widgetContext.$scope.$injector;
const customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
const entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
const importExportService = $injector.get(widgetContext.servicesMap.get('importExport'));
const rxjs = widgetContext.rxjs;

openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    const vm = instance;

    vm.delimiters = [{
        key: ',',
        value: ','
    }, {
        key: ';',
        value: ';'
    }, {
        key: '|',
        value: '|'
    }];

    vm.exampleData = [
        ['389021001233', '12th St 1400', '33.888169', '-118.389693'],
        ['389021001322', 'Manzanita Ln 1501', '33.889536', '-118.381968']
    ];

    vm.columns = [
        { type: 'NAME' },
        { type: 'SERVER_ATTRIBUTE', key: 'address' },
        { type: 'SERVER_ATTRIBUTE', key: 'latitude' },
        { type: 'SERVER_ATTRIBUTE', key: 'longitude' },
        { type: 'SERVER_ATTRIBUTE', key: 'isFull' },
        { type: 'SERVER_ATTRIBUTE', key: 'isLowBattery' },
        { type: 'TYPE' }
    ];

    vm.addEntityFormGroup = vm.fb.group({
        importData: [null, [vm.validators.required]],
        delim: [';', [vm.validators.required]]
    });

    vm.exampleContent = buildExampleContent(vm.addEntityFormGroup.get('delim').value);

    vm.addEntityFormGroup.get('delim').valueChanges.subscribe(value => {
        vm.exampleContent = buildExampleContent(value);
    });

    vm.import = () => {
        const origFile = vm.addEntityFormGroup.get('importData').value;
        const delim = vm.addEntityFormGroup.get('delim').value;
        const parseData = parseCSV(origFile);
        if (parseData === -1) {
            vm.addEntityFormGroup.get('importData').setValue(null);
        } else {
            getEntityGroupByName('Waste Sensors', 'DEVICE').subscribe(group => {
                if (group) {
                    const csvRows = origFile.split(/[\r\n]+/);
                    csvRows.forEach((row, i) => {
                        if (row) {
                            csvRows[i] += (delim + 'false' + delim + 'false' + delim + 'Waste Sensor');
                        }
                    });
                    const updatedFile = csvRows.join("\r\n");
                    const entitiesData = {
                        file: updatedFile,
                        entityGroupId: group.id.id,
                        mapping: {
                            columns: vm.columns,
                            delimiter: delim,
                            header: false,
                            update: true
                        }
                    };
                    importExportService.bulkImportEntities(entitiesData, 'DEVICE', {ignoreErrors: true}).subscribe(result => {
                        setTimeout(() => {
                            widgetContext.updateAliases();
                            widgetContext.showSuccessToast(result.created + (result.created === 1 ? ' bin was' : ' bins were') + ' successfully added! ', 2000, 'top', 'left');
                        }, 1000);
                        vm.dialogRef.close(null);
                    });
                } else {
                    widgetContext.showErrorToast("Target sensors-group doesn't exist!", 'top', 'left', 'importSensorsTarget');
                }
            });
        }
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function getEntityGroupByName(groupName, groupType) {
        const entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
        return entityGroupService.getEntityGroupsByPageLink(entityGroupsPageLink, groupType).pipe(
            rxjs.map((data) => {
                if (data.data.length) {
                    return data.data.find((group) => group.name === groupName);
                } else {
                    return null;
                }
            })
        );
    }

    function buildExampleContent(delim) {
        let content = '';
        vm.exampleData.forEach(dataArray => {
            let result = '<div class="import-info-text">';
            dataArray.forEach((value, index) => {
                result += value;
                if (index !== dataArray.length - 1) {
                    result += ('<span class="delim-value">' + delim + '</span>');
                }
            });
            result += '</div>';
            content += result;
        });
        return content;
    }

    function parseCSV(importData) {
        const config = {
            delim: vm.addEntityFormGroup.get('delim').value,
            header: false
        };
        return convertCSVToJson(importData, config, (messageId, params) => {
            widgetContext.showErrorToast(widgetContext.translate.instant(messageId, params), 'top', 'left', 'importSensorsTarget');
        });
    }

    function convertCSVToJson(csvdata, config, onError) {
        config = config || {};
        const delim = config.delim || ',';
        const header = config.header || false;
        const result = {};
        const csvlines = csvdata.split(/[\r\n]+/);
        const csvheaders = splitCSV(csvlines[0], delim);
        if (csvheaders.length < 2) {
            onError('import.import-csv-number-columns-error');
            return -1;
        }
        const csvrows = header ? csvlines.slice(1, csvlines.length) : csvlines;
        result.headers = csvheaders;
        result.rows = [];
        for (const row of csvrows) {
            if (row.length === 0) {
                break;
            }
            const rowitems = splitCSV(row, delim);
            if (rowitems.length !== result.headers.length) {
                onError('import.import-csv-invalid-format-error', {line: (header ? result.rows.length + 2 : result.rows.length + 1)});
                return -1;
            }
            for (let i = 0; i < rowitems.length; i++) {
                rowitems[i] = convertStringToJSType(rowitems[i]);
            }
            result.rows.push(rowitems);
        }
        return result;
    }

    function splitCSV(str, sep) {
        let foo, x, tl;
        for (foo = str.split(sep = sep || ','), x = foo.length - 1, tl; x >= 0; x--) {
            if (foo[x].replace(/"\s+$/, '"').charAt(foo[x].length - 1) === '"') {
                if ((tl = foo[x].replace(/^\s+"/, '"')).length > 1 && tl.charAt(0) === '"') {
                    foo[x] = foo[x].replace(/^\s*"|"\s*$/g, '').replace(/""/g, '"');
                } else if (x) {
                    foo.splice(x - 1, 2, [foo[x - 1], foo[x]].join(sep));
                } else {
                    foo = foo.shift().split(sep).concat(foo);
                }
            } else {
                foo[x].replace(/""/g, '"');
            }
        }
        return foo;
    }

    function convertStringToJSType(str) {
        if (isNumeric(str.replace(',', '.'))) {
            return parseFloat(str.replace(',', '.'));
        }
        if (str.search(/^(true|false)$/im) === 0) {
            return str.toLowerCase() === 'true';
        }
        if (str === '') {
            return null;
        }
        return str;
    }

    function isNumeric(str) {
        str = str.replace(',', '.');
        return (str - parseFloat(str) + 1) >= 0 && Number(str).toString() === str;
    }
}

