const $injector = widgetContext.$scope.$injector;
const customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
const deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
const attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
const rxjs = widgetContext.rxjs;

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    const vm = instance;

    vm.entityId = entityId;
    vm.entityName = entityName;
    vm.sensor = null;
    vm.attributes = {};

    vm.editEntityFormGroup = vm.fb.group({
        entityName: ['', [vm.validators.required]],
        attributes: vm.fb.group({
            address: ['', [vm.validators.required]],
            latitude: [null],
            longitude: [null]
        })
    });

    getEntityInfo();

    vm.save = () => {
        vm.editEntityFormGroup.markAsPristine();
        const formValues = vm.editEntityFormGroup.value;
        vm.sensor.name = formValues.entityName;
        vm.sensor.label = formValues.entityLabel;
        deviceService.saveDevice(vm.sensor).pipe(
            rxjs.concatMap(() => {
                return saveAttributes();
            })
        ).subscribe(() => {
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
        });
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function getEntityAttributes(attributes) {
        for (let i = 0; i < attributes.length; i++) {
            vm.attributes[attributes[i].key] = attributes[i].value;
        }
    }

    function getEntityInfo() {
        rxjs.forkJoin([
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['latitude', 'longitude', 'address']),
            deviceService.getDevice(entityId.id)
        ]).subscribe(
            (data) => {
                const attrs = data[0];
                vm.sensor = data[1];
                getEntityAttributes(attrs);
                vm.editEntityFormGroup.patchValue({
                    entityName: vm.entityName,
                    attributes: vm.attributes
                });
            }
        );
    }

    function saveAttributes() {
        const attributes = vm.editEntityFormGroup.get('attributes').value;
        const attributesArray = [];
        for (let key in attributes) {
            if (attributes[key] !== vm.attributes[key]) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(vm.entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
}

